var thatall = this;

var fluid = require("universal");

var loader = fluid.getLoader(__dirname);

loader.require("./deviceReporter.js");

// Initialize the Device Reporter at the very beginning and avoid a delay when
// a user is logging in into the system.
//
gpii = fluid.registerNamespace("gpii");
fluid.registerNamespace("gpii.androidDeviceReporter");

var bridge = require("bridge");
var deviceReporter = bridge.load("net.gpii.AndroidDeviceReporterImpl", this);
gpii.androidDeviceReporter.deviceInfoCache = JSON.parse(deviceReporter.getJSONOutput());
