// Currently we need to do this in order to work correctly with
// Anode's JS bridge
var thatall = this;

(function () {
    "use strict";

    var fluid = require("universal");
    var gpii = fluid.registerNamespace("gpii");

    var bridge = require("bridge");

    fluid.registerNamespace("gpii.androidPersistentConfiguration");

    fluid.defaults("gpii.androidPersistentConfiguration.get", {
        gradeNames: "fluid.function",
        argumentMap: {
            settingsarray: 0
        }
    });

    fluid.defaults("gpii.androidPersistentConfiguration.set", {
        gradeNames: "fluid.function",
        argumentMap: {
            settingsarray: 0
        }
    });

    gpii.androidPersistentConfiguration.get = function(settingsarray) {
        var androidPersistentConfig = bridge.load("net.gpii.AndroidPersistentConfigurationImpl", thatall);
        var app = fluid.copy(settingsarray);

        for (var appId in app) {
            for (var j = 0; j < app[appId].length; j++) {
                var settings = app[appId][j].settings;

                for (var setting in settings) {
                    settings[setting] = androidPersistentConfig.get(setting);
                }

                var noOptions = { settings: settings };
                app[appId][j] = noOptions;
            }
        }

        return app;
    };

    gpii.androidPersistentConfiguration.set = function(settingsarray) {
        var androidPersistentConfig = bridge.load("net.gpii.AndroidPersistentConfigurationImpl", thatall);
        var app = fluid.copy(settingsarray);

        for (var appId in app) {
            for (var j = 0; j < app[appId].length; j++) {
                var settings = app[appId][j].settings;

                for (var setting in settings) {
                    var value = settings[setting];
                    var oldValue;

                    // Since this Android API always expects strings as values,
                    // we have to convert them before applying the new value
                    //
                    if (typeof(value) === 'number') {
                        value = value.toString();
                    } else if (typeof(value) === 'boolean') {
                        value = Number(value).toString();
                    }

                    oldValue = androidPersistentConfig.get(setting);
                    androidPersistentConfig.set(setting, value);

                    settings[setting] = {
                        "oldValue": oldValue,
                        "newValue": value
                    };
                }

                var noOptions = { settings: settings};
                app[appId][j] = noOptions;
            }
        }

        return app;
    };

})();
